import React, {useState, useEffect} from "react";
import './App.css';
import Axios from "axios";
import Card from "./components/cards/card";

function App() {
  const [values,SetValues] = useState();
  
  console.log(values);

  const handlechangeValues = value =>{
    SetValues((prevValue) => ({
        ...prevValue,
        [value.target.name]: value.target.value,
    }));
  };

  const handleClickButton = () =>{
    Axios.post("http://localhost:3001/register", {
      name: values.name,
      cost: values.cost,
      category: values.category,
    }).then((response)=>{
      console.log(response)
    });
  };

  useEffect(() =>{
    Axios.get("https//localhost:3001/getCards").then((response)=> {
      console.log(response);
    });
  },[]);

  return (
    <div className="app--container">
      <div className="register--container">
        <h1 className="register--title">Scrim Shop</h1>
        <input type="text" name="name" placeholder="Nome" className="register--input" onChange={handlechangeValues}
        />
        <input type="text" name="const" placeholder="PreÃ§o" className="register--input"
        onChange={handlechangeValues}
        />
        <input type="text" name="category" placeholder="Categoria" className="register--input"
        onChange={handlechangeValues}
        />
        <button className="register--button"
        onClick={() => handleClickButton()}>Cadastrar</button>
      </div>
      <Card>Card</Card>
    </div>
  );
}

export default App;
